import React from 'react';

const Modal = (props) => {

    return (
    <>
    <div>
    <div style={{
        position: 'fixed',

        zIndex: '2',

        width: '80%',

        maxWidth: '800px',

        minHeight: '100vh',

        display: 'flex',

        flexDirection: 'column',

        boxShadow: '0 4px 10px rgba(0,0,0,.2)',

        alignSelf: 'center',

        margin: '40px auto',

        background: 'rgba(68,72,87,.85)',

        borderRadius: '10px'
    }}>
        <h1>Connect your wallet</h1>
        <button onClick={props.connect}>
            MetaMask
        </button>
    </div>
    </div>
    </>
    );
}

export default Modal;
